print to_json( datum_as_geojson($nt_json) );


sub datum_as_geojson
{
    my ( $rs ) = @_;
    my $geo_json = {
    "type" => "FeatureCollection",
    "features" => []
    };
    foreach my $site ( keys %$rs )
    {
        push @{$geo_json->{features}}, {
        "type" => "Feature",
        "geometry" => {
          "type" => "Point",
          "coordinates" => [ $rs->{$site}{'optionsDir'}{'gpsLongitude'}+0, $rs->{$site}{'optionsDir'}{'gpsLatitude'}+0 ]
        },
       "properties" => {
            "title" => $rs->{$site}{post_title},
            "description" => "$rs->{$site}{wlm_address} ",
            "facilities" => {
                ## TODO: 
                #'Toilets' => $site->{'Toilets'},
                #'BBQ'     => $site->{'BBQ'}
            }
        }
      };

    }
    return $geo_json;
}



